import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

// Componente como funcion
/* function Title(props) {
  return <p>{props.title}</p>
}*/

// Componente como arrow function
// const Title = (props) => <p>{props.title}</p>

// Componente como class
class Title extends Component {
  render() {
    return <h2>{this.props.title}</h2>
  }
}

class Text extends Component {
  render () {
    return (
      <div>
        <p>{this.props.text}</p>
        <p>Este es el nro.: {this.props.numero}</p>
        <p>Este es el bool: {JSON.stringfy(this.props.boolean)}</p>
      </div>
    )
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <Title title='Este es el titulo' />
      </header>
      <Text text='Este es el texto' numero={2} boolean={true} />
    </div>
  );
}

export default App;
