import React, {Component} from 'react'

class ComponentA extends Component {
  render() {
    return <h2>Este es el componente A</h2>
  }
}

class ComponentB extends Component {
  render() {
    return <h2>Este es el componente B</h2>
  }
}

/*function useConditionalRendering(mostrarA) {
  if(mostrarA) {
    return <ComponentA />;
  }
  return <ComponentB />
}*/

export default class ConditionalSection extends Component {
  constructor() {
    super();
    this.state = {mostrarA: true};
  }
  render () {
    // Simplificado con ternaria
    // const conditionalComponent = useConditionalRendering(this.state.mostrarA);
    const conditionalComponent = this.state.mostrarA ?
    <ComponentA /> : <ComponentB />;
    return (
    <div>
    <h1>Condicional renderizado</h1>
    {conditionalComponent}
    </div>
  )
}
}
